section.programlisting:
/**
* Start
*
* Sets ball and pads
*
* @param Int $numPlayers
* @return null
*/
start: function(numPlayers) {
    if (!this.playing) {
        this.scores = [0, 0];
        this.playing = true;
        this.leftPaddle.setAuto(numPlayers < 1, this.level(0));
        this.rightPaddle.setAuto(numPlayers < 2, this.level(1));
        this.ball.reset();
        this.runner.hideCursor();
    }
},

/**
* Update
*
* Updates the game according to the DeltaTime
*
* @param Int $dt
* @return null
*/
update: function(dt) {
    this.leftPaddle.update(dt, this.ball);
    this.rightPaddle.update(dt, this.ball);
    if (this.playing) {
        var dx = this.ball.dx;
        var dy = this.ball.dy;
        this.ball.update(dt, this.leftPaddle, this.rightPaddle);
        if (this.ball.dx < 0 && dx > 0)
            this.sounds.ping();
        else if (this.ball.dx > 0 && dx < 0)
            this.sounds.pong();
        else if (this.ball.dy * dy < 0)
            this.sounds.wall();

        if (this.ball.left > this.width)
            this.goal(0);
        else if (this.ball.right < 0)
            this.goal(1);
    }
},

/**
 * declareWinner
 *
 * Declares a winner
 *
 * @param Int $playerNo
 * @return null
 */
 declareWinner: function(playerNo) {
     this.winner = playerNo;
 },
